import "./WorkComponent.css";
import { useState } from 'react';

function WorkComponent() {
  return (
    <div style={{ marginTop: 50 }}>
      <Work
        title="Software Development Intern | PQ Corporation"
        dates="2019 - Present"
      >
        <WorkDescription />
      </Work>
      <Work
        title="Albe"
        dates="2019 - Present"
      >
        <WorkDescription />
      </Work>
      <Work
        title="Not Albe"
        dates="2019 - Present"
      >
        <WorkDescription />
      </Work>
    </div>
  );
}

function Work({
  title,
  children,
  dates
}) {
  const [isActive, setIsActive] = useState(false);

  const handleToggle = () => {
    setIsActive(!isActive);
  };

  return (
    <div className="work">
      <div className="work-header">
        <h6 id="work-title">{title}</h6>
        <div id="sm-container">
          <h6 id="work-dates">{dates}</h6>
          <button id="open-button" onClick={handleToggle}>{isActive ? "-" : "+"}</button>
        </div>
      </div>
      {isActive && <div>{children}</div>}
    </div>
  );
}

function WorkDescription() {
  return (
    <div className="work-desc-container">
      {/* Work description content */}
    </div>
  );
}

export default WorkComponent;
