import "./WorkComponent.css";

import { useState } from 'react';

function WorkComponent() {
  const [activeIndex, setActiveIndex] = useState(0);
  return (
    <>
      <Panel
        title="About"
        isActive={activeIndex === 0}
        onShow={() => setActiveIndex(0)}
        skills={[]}
      >
        Lorem Ipsum Work Description
      </Panel>
      <Panel
        title="Etymology"
        isActive={activeIndex === 1}
        onShow={() => setActiveIndex(1)}
        skills={[]}
      >
        Lorem Ipsum Work Description
      </Panel>
    </>
  );
}

function Panel({
  title,
  children,
  isActive,
  onShow,
  skills
}) {
  return (
    <section className="panel">
      <h3>{title}</h3>
      <div className="work-header">
        <h6 id="work-title">{props.title}</h6>
        <div id="sm-container">
          <h6 id="work-dates">{props.dates}</h6>
          {isActive ? (
            <p>{children}</p>
          ) : (
            <button id="open-button">{isActive ? "+" : "-"}</button>
         )}
         </div>
      </div>
    </section>
  );
}

export default WorkComponent;
