import "./WorkComponent.css";

import { useState } from 'react';
import SkillComponent from "../SkillComponent/SkillComponent";

function WorkComponent() {
  const [activeIndex, setActiveIndex] = useState(0);

  const pq_skills = ["skill", "skill", "skill"];

  return (
    <div className="work-container">
      <>
        <Work
          title="Software Development Intern &ensp; | &ensp; PQ Corporation"
          isActive={activeIndex === 0}
          onShow={() => setActiveIndex(activeIndex === 0 ? null : 0)}
          dates="2019&ensp; - &ensp; Present"
        >
          {<WorkDescription skills={pq_skills}/>}
        </Work>
        <Work
          title="Albe"
          isActive={activeIndex === 1}
          onShow={() => setActiveIndex(activeIndex === 1 ? null : 1)}
          dates="2019&ensp; - &ensp; Present"
        >
          {<WorkDescription/>}
        </Work>
        <Work
          title="Not Albe"
          isActive={activeIndex === 2}
          onShow={() => setActiveIndex(activeIndex === 2 ? null : 2)}
          dates="2019&ensp; - &ensp; Present"
        >
          {<WorkDescription/>}
        </Work>
      </>
    </div>
  );
}

function Work({
  title,
  children,
  isActive,
  onShow,
  skills,
  dates
}) {
  return (
    <div className="work">
      <div className="work-header">
        <h6 id="work-title">{title}</h6>
        <div id="sm-container">
          <h6 id="work-dates">{dates}</h6>
          <button id="open-button" onClick={onShow}>{isActive ? "-" : "+"}</button>
        </div>
      </div>
      {isActive && <p>{children}</p>}
    </div>
  );
}

function WorkDescription({
  location,
  children,
  company,
  skills
}) {

  return (
    <div className="work-desc-container">
      {skills.map(skill => (
              <SkillComponent name={skill}/>
            ))}
    </div>
  );
}

export default WorkComponent;
