import { useState } from "react";
import "./QnA.css";

function QnA() {
  const [expandedQuestion, setExpandedQuestion] = useState(0);
  const [searchTerm, setSearchTerm] = useState("");

  const questions = [
    {
      question: "How do I get started?",
      answer: "When you sign up, you'll start with the Free plan. It's ideal for new teams and allows unlimited team members, but only 1 active editable project at a time. For more advanced features, check out our Basic, Premium, or Enterprise plans."
    },
    {
      question: "What is included in the Free Plan?",
      answer: "The Free plan includes unlimited team members, 1 active editable project, basic templates, and community support. Perfect for getting started with your first project."
    },
    {
      question: "How do I cancel my membership?",
      answer: "You can cancel your membership at any time from your account settings. Go to Account > Billing > Cancel Subscription. Your access will continue until the end of your current billing period."
    },
    {
      question: "How do I transfer my membership to a different account?",
      answer: "To transfer your membership, contact our support team with both account details. We'll help you migrate your data and subscription to the new account within 24-48 hours."
    },
    {
      question: "What is the refund policy?",
      answer: "We offer a 30-day money-back guarantee for all paid plans. If you're not satisfied, contact our support team within 30 days of your purchase for a full refund."
    }
  ];

  const filteredQuestions = questions.filter(q =>
    q.question.toLowerCase().includes(searchTerm.toLowerCase()) ||
    q.answer.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const toggleQuestion = (index) => {
    setExpandedQuestion(expandedQuestion === index ? -1 : index);
  };

  return (
    <div className="faq-container">
      <div className="faq-header">
        <div className="search-container">
          <input
            type="text"
            placeholder="Search for a question"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="search-input"
          />
        </div>
      </div>

      <div className="questions-container">
        {filteredQuestions.map((item, index) => (
          <div key={index} className={`question-panel ${expandedQuestion === index ? 'expanded' : ''}`}>
            <div className="question-header" onClick={() => toggleQuestion(index)}>
              <h3 className="question-text">{item.question}</h3>
              <button className="toggle-button">
                {expandedQuestion === index ? '✕' : '+'}
              </button>
            </div>
            {expandedQuestion === index && (
              <div className="answer-content">
                <p>{item.answer}</p>
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
}

export default QnA;
