import { useState } from "react";
import "./RSVP.css";

function RSVP() {
  const [name, setName] = useState("");
  const [rsvpStatus, setRsvpStatus] = useState("will-attend");
  const [message, setMessage] = useState("Can't wait!!!");

  const handleRsvpChange = (status) => {
    setRsvpStatus(status);
  };

  return (
    <div className="container">
      <div className="rsvp-form">
        <input
          type="text"
          className="name-input"
          placeholder="Kierra Gentry"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />

        <div className="rsvp-buttons">
          <button
            className={`rsvp-btn ${rsvpStatus === "will-attend" ? "active" : ""}`}
            onClick={() => handleRsvpChange("will-attend")}
          >
            Will Be There
          </button>
          <button
            className={`rsvp-btn ${rsvpStatus === "cant-attend" ? "active" : ""}`}
            onClick={() => handleRsvpChange("cant-attend")}
          >
            Can't Make It
          </button>
        </div>

        <p className="message-label">Anything you want the couple to know?</p>

        <textarea
          className="message-input"
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          placeholder="Can't wait!!!"
        />
      </div>
    </div>
  );
}

export default RSVP;
