import "./App.css";
import {Link, Element} from 'react-scroll';
import { configure } from "@testing-library/react";
import Home from "./components/Home/Home";
import About from "./components/About/About";
import Projects from "./components/Projects/Projects";
import Experience from "./components/Experience/Experience";
import React, {Fragment, useEffect, useState} from 'react';
import HeaderComponent from "./components/HeaderComponent/HeaderComponent";
import SucculentComponent from './components/SucculentComponent/SucculentComponent';
import { motion } from 'framer-motion';

function App() {
  const [mousePosition, setMousePosition] = useState({
    x: 0,
    y: 0,
  })

  useEffect(() => {
    const mouseMove = (e) => {
      setMousePosition({
        x: e.clientX,
        y: e.clientY
      })
    }
    window.addEventListener("mousemove", mouseMove);
    return () => {
      window.removeEventListener("mousemove", mouseMove);
    }
  }, [])

  const variants = {
    default: {
      x: mousePosition.x,
      y: mousePosition.y,

    }
  }

  const navItems = [
    {
      id: 1,
      title: "Home",
      component: <Home/>,
      desc: "",
    },
    {
      id: 2,
      title: "About",
      component: <About/>,
      desc: "Here you will find more personal information about me as well as some of my programming skills",
    },
    {
      id: 3,
      title: "Projects",
      component: <Projects/>,
      desc: "Here you will find some of the projects I have done along with what technologies were used to create them",
    },
    {
      id: 4,
      title: "Experience",
      component: <Experience/>,
      desc: "Here are my previous work experiences",
    }
  ]

  return (
    <div className="App">
      <header>
        <nav>
          <ul>
            {navItems.map((item) => (
              <li>
                <Link to={item.title} smooth="true" offset={-60} duration={1000} activeClass='active' spy={true}>{item.title}</Link>
              </li>
            ))}
          </ul>
        </nav>
      </header>

      <main>
        {navItems.map(item => (
          <Element offset={500} name={item.title}>
            <div className={`${item.title} content`}>
              {item.title != "Home" &&
              <HeaderComponent name={item.title} subheader={item.desc}/>
              }
              <React.Fragment key={item.id}>
                { item.component }
              </React.Fragment>
            </div>
          </Element>
        ))}
        <SucculentComponent/>
      </main>
      <motion.div className="cursor" variants={variants} animate="default"/>
    </div>
  );
}



export default App;
