import "./App.css";
import {Link, Element} from 'react-scroll';
import { configure } from "@testing-library/react";
import Home from "./components/Home/Home";
import Story from "./components/Story/Story";
import WeddingParty from "./components/WeddingParty/WeddingParty";
import RSVP from "./components/RSVP/RSVP";
import Registry from "./components/Registry/Registry";
import React, {Fragment, useEffect, useState} from 'react';
import SucculentComponent from './components/SucculentComponent/SucculentComponent';
import InitialLogo from "./images/initial_logo.png";

function App() {
  const [isScrolled, setIsScrolled] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      const scrollTop = window.scrollY;
      setIsScrolled(scrollTop > 10);
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const navItems = [
    {
      id: 1,
      title: "Home",
      component: <Home/>,
      desc: "",
    },
    {
      id: 2,
      title: "Our Story",
      component: <Story/>,
      desc: "Here you will find more personal information about me as well as some of my programming skills",
    },
    {
      id: 3,
      title: "Wedding Party",
      component: <WeddingParty/>,
      desc: "Meet the special people who will be standing with us on our big day",
    },
    {
      id: 4,
      title: "Registry",
      component: <Registry/>,
      desc: "Here are my previous work experiences",
    },
    {
      id: 5,
      title: "RSVP",
      component: <RSVP/>,
      desc: "Here are my previous work experiences",
    }
  ]

  return (
    <div className="App">
      <header className={`${isScrolled ? 'scrolled' : ''}`}>
        <nav>
          <div className="nav-logo">
            <img className="nav-logo-image" src={InitialLogo} alt="A&D Logo" />
          </div>
          <ul>
            {navItems.map((item) => (
              <li key={item.title}>
                <Link to={item.title} smooth="true" offset={-60} duration={750} activeClass='active' spy={true}>{item.title}</Link>
              </li>
            ))}
          </ul>
        </nav>
      </header>

      <main>
        {navItems.map(item => (
          <Element key={item.title} name={item.title}>
            <div className={`${item.title} content`}>
              <React.Fragment key={item.id}>
                { item.component }
              </React.Fragment>
            </div>
          </Element>
        ))}
        <SucculentComponent/>
      </main>
    </div>
  );
}



export default App;
