import React, { useEffect, useMemo, useState } from "react";
import { FiSearch, FiUser } from "react-icons/fi";
import logo from "../../images/initial_logo.png";
import usPhoto from "../../images/us.jpg";
import "./Home.css";

function scrollToId(id) {
  const el = document.getElementById(id);
  if (!el) return;
  el.scrollIntoView({ behavior: "smooth", block: "start" });
}

export default function Home() {
  const [scrolled, setScrolled] = useState(false);

  useEffect(() => {
    const onScroll = () => setScrolled(window.scrollY > 8);
    onScroll();
    window.addEventListener("scroll", onScroll, { passive: true });
    return () => window.removeEventListener("scroll", onScroll);
  }, []);

  const navItems = useMemo(
    () => [
      { label: "Home", id: "home" },
      { label: "Wedding", id: "wedding" },
      { label: "Our Story", id: "our-story" },
      { label: "About", id: "about" },
      { label: "Contact", id: "contact" },
    ],
    []
  );

  return (
    <>
      <header className={scrolled ? "scrolled" : ""}>
        <nav className="w-nav" aria-label="Primary">
          <ul className="w-nav-left">
            {navItems.map((item) => (
              <li key={item.id}>
                <a
                  href={`#${item.id}`}
                  onClick={(e) => {
                    e.preventDefault();
                    scrollToId(item.id);
                  }}
                >
                  {item.label}
                </a>
              </li>
            ))}
          </ul>

          <div className="nav-logo" aria-hidden="true">
            <img src={logo} alt="" />
          </div>

          <ul className="w-nav-right">
            <li>
              <a
                href="#login"
                onClick={(e) => {
                  e.preventDefault();
                }}
                className="w-nav-iconLink"
                aria-label="Log In"
              >
                <FiUser />
                <span className="w-nav-iconText">Log In</span>
              </a>
            </li>
            <li>
              <a
                href="#search"
                onClick={(e) => e.preventDefault()}
                className="w-nav-iconLink"
                aria-label="Search"
                title="Search"
              >
                <FiSearch />
              </a>
            </li>
            <li>
              <a
                href="#rsvp"
                onClick={(e) => {
                  e.preventDefault();
                  scrollToId("rsvp");
                }}
                className="w-nav-rsvp"
              >
                RSVP
              </a>
            </li>
          </ul>
        </nav>
      </header>

      <main>
        <section
          id="home"
          className="hero"
          style={{ backgroundImage: `url(${usPhoto})` }}
        >
          <div className="hero-inner">
            <p className="hero-kicker">We Are Getting Married</p>
            <h1 className="hero-title">Dominic &amp; Anna</h1>
            <p className="hero-copy">
              We are so excited to celebrate with you. Please RSVP when you can
              so we can plan accordingly.
            </p>
            <a
              href="#rsvp"
              className="hero-cta"
              onClick={(e) => {
                e.preventDefault();
                scrollToId("rsvp");
              }}
            >
              Will You Attend
            </a>
          </div>
        </section>

      </main>
    </>
  );
}
