import React, { useEffect, useMemo, useState } from "react";
import { FiSearch, FiUser } from "react-icons/fi";
import logo from "../../images/initial_logo.png";
import usPhoto from "../../images/us.jpg";
import "./Home.css";

export default function Home() {

  useEffect(() => {
    const onScroll = () => setScrolled(window.scrollY > 8);
    onScroll();
    window.addEventListener("scroll", onScroll, { passive: true });
    return () => window.removeEventListener("scroll", onScroll);
  }, []);

  return (
    <>

      <main>
        <section
          id="home"
          className="hero"
          style={{ backgroundImage: `url(${usPhoto})` }}
        >
          <div className="hero-inner">
            <p className="hero-kicker">We Are Getting Married</p>
            <h1 className="hero-title">Dominic &amp; Anna</h1>
            <p className="hero-copy">
              We are so excited to celebrate with you. Please RSVP when you can
              so we can plan accordingly.
            </p>
            <a
              href="#rsvp"
              className="hero-cta"
              onClick={(e) => {
                e.preventDefault();
                scrollToId("rsvp");
              }}
            >
              Will You Attend
            </a>
          </div>
        </section>

      </main>
    </>
  );
}
